import React, { useEffect, useState } from "react";
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Logo from '../assets/logo.svg';
// import ModalReview from './Modals/ModalReview';
import ModalInput from './Modals/ModalInput';
import List from './List';
import styled from 'styled-components';
import axios from "axios"; 

const Home = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [review, setReview] = useState({
    movie_title: "",
  });
  const [reviews, setReviews] = useState(null);

  const fetchReviews = async () => {
    const { data } = await axios.get("http://localhost:3001/reviews");
    setReviews(data);
  };

  const onSubmitHandler = (review) => {
    axios.post("http://localhost:3001/reviews", review);
  };

  useEffect(() => {
    fetchReviews();
  }, []);

  const onClickButton = () => {
    setIsOpen(true);
  };

  const navigate = useNavigate();

  return (
    <>
      <div className='home_wrap'>
        <img className='logo' src={Logo} alt='Î°úÍ≥†' />

        <div className='list_button'>
          <button className='now_movie_list' onClick={() => {
            navigate('/list')
          }}>
            ÏßÄÍ∏àÍπåÏßÄÏùò ÏòÅÌôîÎì§
          </button>

          {/* <button type="button" onClick="refreshPage()">Close</button>

function refreshPage(){ window.location.reload() */}
          <form
        onSubmit={(e) => {
          // Î≤ÑÌäº ÎàåÎ†ÄÏùÑÎïå ÏÉàÎ°úÍ≥†Ïπ®
          // window.location.reload()
          // üëá submitÌñàÏùÑ Îïå Î∏åÎùºÏö∞Ï†ÄÏùò ÏÉàÎ°úÍ≥†Ïπ®ÏùÑ Î∞©ÏßÄÌï©ÎãàÎã§. 
          // e.preventDefault();
          onSubmitHandler(review);
          //ÏßàÎ¨∏: window.location.reload() , e.preventDefault() Ïì∞ÎÇò ÏïàÏì∞ÎÇò ÏÉàÎ°úÍ≥†Ïπ®Ïù¥ ÎêòÎäî Î¨∏Ï†ú. Ï∞®Ïù¥Í∞Ä Î¨¥ÏóáÏù∏ÏßÄ.
        }}
      >
        <input
          type="text"
          onChange={(ev) => {
            const { value } = ev.target;
            setReview({
              ...review,
              movie_title: value,
            });
          }}
        />
        <button>Ï∂îÍ∞ÄÌïòÍ∏∞</button>
      </form>

      <div>
        {reviews?.map((review) => (
          <div key={review.id}>{review.movie_title}</div>
        ))}
      </div>


          {/* <button className='review_list' onClick={onClickButton}>
            Î¶¨Î∑∞Ïì∞Îü¨ Í∞ÄÍ∏∞
          </button> */}
          {isOpen && (
            <ModalInput
              open={isOpen}
              onClose={() => {
                setIsOpen(false);
              }}
            />
          )}
        </div>
      </div>
    </>
  );
};

            const Button = styled.button`
            font-size: 14px;
            padding: 10px 20px;
            border: none;
            background-color: #fa9f98;
            border-radius: 10px;
            color: white;
            font-style: italic;
            font-weight: 200;
            cursor: pointer;
            &:hover {
              background-color: #fac2be;
            }
          `;

          const AppWrap = styled.div`
            text-align: center;
            margin: 50px auto;
          `;


export default Home;
