  const fetchDetailReviews = async () => {
    const { data } = await axios.get("http://localhost:3001/reviews",{Id:param?.id});
    setDetailReviews(data);
    console.log('bbbb');
    console.log(data);
    console.log(param.id);
  };

  const addComment = (e) => {
    e.preventDefault();
    if (comment === ""){
        alert("댓글을 작성 해주세요.")
    }
    dispatch(
        __postComments({
            // reviewsId:params,
            reviewsId:params,
            comment:comment
        })
    )
